import React from 'react'
// import axios, { post } from 'axios';
import {
    Card,
    CardBody,
    CardTitle,
    Input,
} from 'reactstrap';
import { BrowserRouter, Link } from "react-router-dom";

class Content extends React.Component {
	constructor(props) {
    super(props)
    this.state = {
      todos: []
    };  
  }
  async componentDidMount() {
    try {
      const res = await fetch('http://localhost:3000/members');
      const todos = await res.json();
      this.setState({
        todos
      });
    } 
    catch (e) {
      console.log(e);
    }
  }  
  handleClick = event => {
  event.currentTarget.classList.toggle('active');
}
  render() {
    return (
    	<BrowserRouter>
	      <Card>
	        <CardBody>
	          <CardTitle>Feeds1</CardTitle>
	            <div>
	              {this.state.todos.map((item, i) => (
	                <div className="item" onClick={this.handleClick} key={i}>
	                  <h1>{item.real_name}</h1>
	                  <Link to={`/customer-details/${item.id}`}>{item.real_name}</Link>
	                </div>

	              ))}
	            </div>
	        </CardBody>
	      </Card>
      </BrowserRouter>
    );
  }

}
export default Content
